import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate instead of useHistory
import '../assets/css/LoginSignup.css';
import user_icon from '../assets/images/person.png';
import email_icon from '../assets/images/email.png';
import password_icon from '../assets/images/password.png';

const SignUp = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate(); // Use useNavigate instead of useHistory

  const handleSignup = () => {
    // Simple validation
    if (!name || !email || !password || password !== confirmPassword) {
      setErrorMessage('Please fill in all fields and ensure passwords match');
    } else {
      // Mock signup (replace with actual signup logic)
      // For simplicity, assume signup is always successful
      setErrorMessage('');
      localStorage.setItem('isLoggedIn', 'true'); // Simulate authentication by storing a token
      navigate('/'); // Redirect to the home page using useNavigate
    }
  };

  return (
    <div className="container">
      <div className="header">
        <div className="text">Sign Up</div>
        <div className="underline"></div>
      </div>
      <div className="inputs">
        <div className="input">
          <img src={user_icon} alt="" />
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div className="input">
          <img src={email_icon} alt="" />
          <input
            type="email"
            placeholder="Email Id"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="input">
          <img src={password_icon} alt="" />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="input">
          <img src={password_icon} alt="" />
          <input
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </div>
      </div>
      <div className="error-message">{errorMessage}</div>
      <div className="submit-container">
        <div className="submit">
          <Link to="/login">Login</Link>
        </div>
        <div className="submit" onClick={handleSignup}>
          Sign Up
        </div>
      </div>
    </div>
  );
};

export default SignUp;
